mapper.select:
select

from device


        and device_name = #{deviceName,jdbcType=VARCHAR}


        and category_id = #{categoryId,jdbcType=INTEGER}


        and user_id = #{userId,jdbcType=INTEGER}


        and `using` = #{using,jdbcType=BOOLEAN}


        and create_time = #{createTime,jdbcType=TIMESTAMP}


        and update_time = #{updateTime,jdbcType=TIMESTAMP}

mapper.insert:
insert into device (device_name, category_id, user_id, 
  `using`, create_time, update_time
  )
values (#{deviceName,jdbcType=VARCHAR}, #{categoryId,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, 
  #{using,jdbcType=BOOLEAN}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )
